// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Mock function to simulate authentication
const mockAuthenticate = (username, password) => {
  // Replace with actual authentication logic
  return username === 'user' && password === 'password';
};

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    
    // Perform authentication logic here
    if (mockAuthenticate(username, password)) {
      // Set authentication token or user state in local storage or context
      localStorage.setItem('isAuthenticated', 'true');
      navigate('/spreadsheet'); // Redirect to the spreadsheet after successful login
    } else {
      setError('Invalid username or password');
    }
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <button type="submit">Login</button>
        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
}

export default Login;
