// src/components/Grid.js
import React, { useState, useEffect } from 'react';
import './Grid.css'; // Import custom styles

const Grid = ({ onCellChange, activeCell, setActiveCell }) => {
  const [cells, setCells] = useState({});
  const [editingCell, setEditingCell] = useState(null);

  // Handle double click to edit a cell
  const handleDoubleClick = (row, col) => {
    setEditingCell({ row, col });
  };

  // Handle change in cell value
  const handleChange = (e, row, col) => {
    const value = e.target.value;
    setCells((prevCells) => ({
      ...prevCells,
      [`${row}-${col}`]: value
    }));
  };

  // Handle when user presses enter or clicks out of the cell
  const handleBlur = (row, col) => {
    setEditingCell(null);
    const key = `${row}-${col}`;
    if (cells[key] !== undefined) {
      onCellChange(row, col, cells[key]);
    }
  };

  // Handle selection of a cell
  const handleCellClick = (row, col) => {
    setActiveCell({ row, col });
  };

  return (
    <div className="grid">
      {Array.from({ length: 20 }, (_, row) => (
        <div key={row} className="grid-row">
          {Array.from({ length: 10 }, (_, col) => {
            const key = `${row}-${col}`;
            return (
              <div
                key={key}
                className={`grid-cell ${activeCell?.row === row && activeCell?.col === col ? 'active' : ''}`}
                onDoubleClick={() => handleDoubleClick(row, col)}
                onClick={() => handleCellClick(row, col)}
              >
                {editingCell?.row === row && editingCell?.col === col ? (
                  <input
                    type="text"
                    value={cells[key] || ''}
                    onChange={(e) => handleChange(e, row, col)}
                    onBlur={() => handleBlur(row, col)}
                    autoFocus
                  />
                ) : (
                  cells[key] || ''
                )}
              </div>
            );
          })}
        </div>
      ))}
    </div>
  );
};

export default Grid;
